def connect():    import network    import time    ip        = "192.168.10.201"    subnet    = '255.255.255.0'    gateway   = '192.168.10.1'    dns       = '8.8.8.8'    ssid      = "Aschi"    password  =  "2482694733144611"     station = network.WLAN(network.STA_IF)     if station.isconnected() == True:        print("Schon verbunden")        return     station.active(True)    station.ifconfig((ip,subnet,gateway,dns))    station.connect(ssid,password)    station.isconnected()    while not station.isconnected():        time.sleep(0.2)        print("*")        #pass     print("Wifi steht!")    print(station.ifconfig()) def disconnect():    import network    station = network.WLAN(network.STA_IF)    station.disconnect()    station.active(False)    print("WiFi aus!")